import React,{Component} from 'react';
import {API} from 'aws-amplify';
export default class ViewPost extends Component{
    constructor(props){
        super(props)
        this.state = {
            post:null,
            content:'',
            loaded:false
        }
    }
    componentDidMount(){
        this.onLoad();
    }
    async loadNote() {
        let postId = this.props.match.params.id;
        console.log(postId);
        return API.get("posts", `/posts/${postId}`);
    }
    async onLoad(){
        let post = await this.loadNote();
        console.log(post);
        this.setState({post:post, loaded:true})
    }
    render() {
        console.log(this.state);
        return(
        <div>
            <p>This views a Post</p>
            {this.state.loaded &&
            <div>
                <p>Title: {this.state.post.title}</p>     
                <p>Name: {this.state.post.firstname + " " + this.state.post.lastname}</p>     
                <p>Content: {this.state.post.content}</p>     
                <p>Created: {new Date(this.state.post.createdAt).toDateString()}</p>
                <p>Created: {new Date(this.state.post.updatedAt).toDateString()}</p>

            </div>
        }
        </div>
        );
    }
}
