import React, {Component} from 'react';
import ReactLoading from 'react-loading';
import { Auth } from "aws-amplify";
import { API } from "aws-amplify";
import { Storage } from "aws-amplify";
import ImageUploader from 'react-images-upload';
export default class Search extends Component{
    constructor(props){
        super(props)
        this.state = {
            name:'',
            pictures: [],
        }
        this.onChange = this.onChange.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
        this.onDrop = this.onDrop.bind(this);
    }
    componentDidMount(){
        this.setState({
            users:['testuser'],
            username:'testuser'
        })
        try{
        Auth.currentAuthenticatedUser()
        .then(user => {
            console.log("HI");
            console.log(user);
            this.setState({noUser:false, name:user.attributes.name, lastname:user.attributes.family_name});})
        }catch(e){
            console.log(e);
        }
    }
    onChange(e){
        this.setState({
            [e.target.id]:e.target.value
        });
    }
    onSubmit(e){
        e.preventDefault();
        console.log(this.state.name);
    }
    onChangeHandler=event=>{
        let file = event.target.files[0];
    if (file && !file.name) {
       window.alert("Please select a file");
       return false;
    }
    if (file.size > 10e6) {
      window.alert("Please upload a file smaller than 10 MB");
      event.target.value = null;
      return false;
    }
      console.log(event.target.files[0]);
    this.setState({
      selectedFile: event.target.files[0],
      hasFile: true,
    });

  }
  onDrop(pictureFiles, pictureDataURLs) {
    this.setState({
        pictures: this.state.pictures.concat(pictureFiles),
    });
}
    render() {
        return(
          <div className="text-center text-2xl">
            <p>Search Page</p>
            <section class="bg-blue-500 h-50 p-8">
              <div class="container mx-auto py-8">
                <input class="w-full h-16 px-3 rounded mb-8 focus:outline-none focus:shadow-outline text-xl px-8 shadow-lg" type="search" placeholder="Search By Name..."/>
                <nav class="flex">
                  <a class="underline text-white py-3 px-4 font-medium mr-3 bg-indigo hover:bg-indigo-darker hover:text-gray-400" href="#">Upload By Photo</a>
                  <ImageUploader
                	buttonText='Choose image'
                	onChange={this.onDrop}
                	imgExtension={['.jpg', '.gif', '.png', '.gif']}
                	maxFileSize={10e6}
            />
                  <a class="underline text-white py-3 px-4 font-medium mx-3 bg-indigo-darker hover:bg-indigo hover:text-gray-400" href="#">Upload by Youtube Link</a>
              </nav>
          </div>
      </section>
          </div>
        );
    }
}
