import React, { useState } from "react";
import './styles/tailwind.css';
import {BrowserRouter as Router,Route,Switch} from 'react-router-dom';
import CreateUser from './components/create-user';
import CreatePost from './components/create-post';
import EditPost from './components/edit-post';
import Posts from './components/load-post';
import NavBar from './components/bar';
import Error from './components/error';
import { createBrowserHistory } from 'history';
import AppliedRoute from "./components/AppliedRoute";
const browserHistory = createBrowserHistory();

class App extends React.Component {	
    constructor(props){
        super(props)    
        this.state ={
            isAuthenticated:false
        }
        this.hasAuthenticated = this.hasAuthenticated.bind(this);
    }

    hasAuthenticated(){
        this.setState({isAuthenticated:true})
    }
    handleLogout() {
        this.setState({isAuthenticated:false})
      }
	render() {
    	return (
            <div>
            <Router path="/App" history={browserHistory} >
            <NavBar/>
            <br/>
                <Switch>

                <AppliedRoute exact path="/" component={Posts} appProps={{ isAuthenticated:this.state.isAuthenticated, hasAuthenticated: this.state.hasAuthenticated }}/>
                <AppliedRoute path="/edit/:id" exact component={EditPost}/>
                <AppliedRoute path="/create" exact component={CreatePost}/>
                <AppliedRoute path="/user" exact component={CreateUser}/>
                <Route component={Error}/>
                </Switch>
            </Router >
            </div>
        )
	}
}

export default App;
