        await this.setState({
            [e.target.id]:e.target.value
        });
        console.log(this.state.name);
    }
    async onSubmitName(e){
        e.preventDefault();
        if(this.state.name.length === 0){
            alert("Please input something");
            return;
        }
        console.log(this.state.name);
