import React,{Component} from 'react';
import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";
export default class CreatePost extends Component{
    constructor(props){
        super(props)
        this.state = {
            username:'',
            content:'',
            title:'',
            date: new Date(),
            price:0,
            image:'',
            users:[],
        }
        this.onChange = this.onChange.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
    }
    componentDidMount(){
        this.setState({
            users:['testuser'],
            username:'testuser'
        })
    }
    onChange(e){
        this.setState({
            [e.target.id]:e.target.value
        });
    }
    onSubmit(e){
        e.preventDefault();
        const post  ={
            username:this.state.username,
            content:this.state.content,
            title:this.state.title,
            price:this.state.price,
            image:this.state.image,
            date:this.state.date,
        }
        console.log(post);
        window.location = '/'
    }
    render() {
        return(
            <div className="flex items-center h-full w-full">
            <div className="container-sm border border-solid max-w-sm mx-auto mt-1/10 bg-white rounded">
                <p className="w-full block font-sans text-2xl font-bold text-center justify-center mb-14">
      Create New Exercise Log
        </p>
      <form onSubmit={this.onSubmit}>
        <div className="items-center flex justify-center"> 
          <label>Username: </label>
          <select ref="userInput"
              required
              className="form-control"
              value={this.state.username}
              onChange={this.onChangeUsername}>
              {
                this.state.users.map(function(user) {
                  return <option 
                    key={user}
                    value={user}>{user}
                    </option>;
                })
              }
          </select>
        </div>
        <div className="items-center flex justify-center"> 
          <label>Description: </label>
          <input  type="text"
              required
              className="form-control"
              value={this.state.description}
              onChange={this.onChangeDescription}
              />
        </div>
        <div className="items-center flex justify-center">
          <label>Duration (in minutes): </label>
          <input 
              type="text" 
              className="form-control"
              value={this.state.duration}
              onChange={this.onChangeDuration}
              />
        </div>
        <div className="items-center flex justify-center">
          <label>Date: </label>
          <div>
            <DatePicker
              selected={this.state.date}
              onChange={this.onChangeDate}
            />
          </div>
        </div>

        <div className="items-center flex justify-center">
          <input type="submit" value="Create Exercise Log" className="px-4 py-2 rounded border border-solid border-green-400" />
        </div>
      </form>
    </div>
    </div>

        );
    }
}
